import axios from "axios";
import { useEffect, useState } from "react";
axios.defaults.baseURL = process.env.REACT_APP_SERVER_DOMAIN;
/**custom hook */
export default function useFetch(query) {
  const [getData, setGetData] = useState({
    isloading: false,
    apiData: undefined,
    status: null,
    serverError,
  });
  useEffect(() => {
    if (!query) return;
    const fetchData = async () => {
      try {
        setGetData((prev) => ({
          ...prev,
          isloading: true,
        }));
        const { data, status } = await axios.get(`/api/${query}`);
        if (status === 201) {
          setGetData((prev) => ({
            ...prev,
            isloading: false,
          }));
          setGetData((prev) => ({
            ...prev,
            apiData: data,
            status: status,
          }));
        }
        setGetData((prev) => ({
          ...prev,
          isloading: false,
        }));
      } catch (error) {
        setGetData((prev) => ({
          ...prev,
          isloading: false,
          serverError: error,
        }));
      }
    };
    fetchData();
  }, [query]);
  
}
